class StatisticsTests:
    def __init__(self, state):
        """
        Initialize parameters
        :param state: run mode: 1 - all genes, 2 - top genes
        """
        self.state = state
        self.output_folder_name = "PostStatisticsTests"

    @staticmethod
    def _file_name_escaping(filename):
        """
        Make escaping for file names (i.e: omit '|' or '\'.
        :param filename: filename to escape
        :return: escaped filename
        """
        valid_chars = "-_.() %s%s" % (string.ascii_letters, string.digits)
        return ''.join(c for c in filename if c in valid_chars)

    def run(self, test_name, data, model):
        """
        Run statistics tests
        :param test_name: name of running test
        :param data: data handler
        :param model: model handler to use
        """
        logging.info("Starting statistics tests: %s", str(test_name))
        output_folder = os.path.join(config.config_map['output_folder'], self.output_folder_name)
        if not os.path.isdir(output_folder):
            os.makedirs(output_folder)

        data_df, info_df, reference_points = data.get_all_data_and_info()

        # Set additional needed columns
        info_df = info_df.copy()
        info_df['arithmetic'] = 'original'
        info_df['correctly_classified'] = 'unknown'
        info_df['is_decoded'] = 0

        # Create encoded and decoded data sets and their info
        encoded_data_df = model.predict_latent_space(data_df)
        encoded_info_df = info_df.copy()
        encoded_info_df['arithmetic'] = 'encoded'
        encoded_data_df.index = 'encoded_' + encoded_data_df.index
        encoded_info_df.index = 'encoded_' + encoded_info_df.index
        encoded_data_and_info = (encoded_data_df, encoded_info_df)

        tumor = config.config_map['leave_out_tissue_name']
        perturbation = config.config_map['leave_out_perturbation_name']

        latent_space_arithmetic = LatentSpaceArithmetic(data, model)

        # Calculate new cloud
        calculated_tuple = \
            latent_space_arithmetic.calculate_perturbations_effect_and_info(
                encoded_data_and_info, tumor, [perturbation], config.config_map['perturbation_times'])
        if self.state == 2:
            encoded_calculated_df, decoded_calculated_df, calculated_info_df, reference_points = calculated_tuple
            current_top_genes = top_genes[perturbation][tumor]
            decoded_calculated_df = decoded_calculated_df[current_top_genes]
            calculated_tuple = (encoded_calculated_df, decoded_calculated_df, calculated_info_df, reference_points)

        latent_space_df = model.predict_latent_space(data_df)

        # First create treated tuple
        treated_info_df = info_df[(info_df.tumor == tumor) & (info_df.perturbation == perturbation)]
        encoded_treated_data_df = latent_space_df.loc[treated_info_df.index]
        original_treated_data_df = data_df.loc[treated_info_df.index]
        if self.state == 2:
            current_top_genes = top_genes[perturbation][tumor]
            original_treated_data_df = original_treated_data_df[current_top_genes]

        config.config_map['pictures_folder'] = output_folder
        correlation_calculator = StatisticsCalculator(
            (encoded_treated_data_df, original_treated_data_df, treated_info_df))
        statistics_results = \
            correlation_calculator.do_statistical_tests(calculated_tuple, '{} {} {}'.format("Post", self._file_name_escaping(tumor), perturbation))

        results_file_path = os.path.join(config.config_map['output_folder'], 'results.json')
        with open(results_file_path, 'r') as fp:
            results = json.load(fp)
        results['Arithmetic']['statistics_results'] = statistics_results
        with open(results_file_path, 'w') as fp:
            json.dump(results, fp, indent=4, default=(lambda o: 'Unserializable'))

        logging.info("End statistics tests: %s", str(test_name))
